import React, { Component } from 'react';
import fire from 'firebase';
import Webcam from "react-webcam";


class ChatBot extends Component {

  constructor(){
    super();
      this.logOut = this.logOut.bind(this);
      this.sendData = this.sendData.bind(this);
      this.state = {
        response: ""
      }
  }


  logOut(){
    fire.auth().signOut();
  }

  setRef = webcam => {
    this.webcam = webcam;
  };

  capture = () => {
      const imageSrc = this.webcam.getScreenshot();
     // f();
      this.getMood(imageSrc);

  };

  sendData(ev){
    ev.preventDefault();

    const data = "SENT PROPERLY";

    fetch('http://localhost:4000/upload', {
        method: 'POST',
        body: data
    }).then(response => {
        response.json().then(body => {
            this.setState({response: `http://localhost:4000/${body.file}`});
        });
    });
    }

  getMood(imageData){
    const AWS = require('aws-sdk');
    AWS.config.loadFromPath('./config.json');
    AWS.config.update({region:'us-east-1'});
    const uuid = require('uuid');
    const params = {
        Attributes: [ "ALL" ],
        Image: {
            S3Object: {
                Bucket: "get-schwifty-fedb0335-6715-4913-b4f7-cc0800e7ae56",
                Name: "photo.jpg"
            }
        }
      };


    const rekognition = new AWS.Rekognition();
    rekognition.detectFaces(params, function (err,data) {if (err) console.log(err, err.stack); // an error occurred
    else     console.log(data);        });
  }





  render() {
   return (

    <div >
    <Webcam
        style={{display: 'none'}}
        audio={false}
        height={350}
        ref={this.setRef}
        screenshotFormat="image/jpeg"
        width={350}
    />

    <button onClick={this.capture}>SEND DATA </button>
    <button onClick={this.sendData}>Play a song </button>
      <button onClick = {this.logOut} >logout</button>
    </div>
  );
}
}

export default ChatBot;
