import React, { Component } from 'react';
import fire from 'firebase';
import Webcam from "react-webcam";


class ChatBot extends Component {

    state = {
        response: '',
        post: '',
        responseToPost: '',
        song: 'All along the watchtower'
    };



    handleSubmit = async () => {
        const response = await fetch('http://localhost:4000/api/play', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ post: this.state.post }),
        });
        const body = await response.text();
        this.setState({ responseToPost: "Here is a "+ body + " music" });
    };

    handleLyricsSearch = async() =>{
        const response = await fetch('http://localhost:4000/api/lyrics',{
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ post: this.state.song})
        });
        const body = await response.text();
        this.setState({ responseToPost: body});
    };

  constructor(){
    super();
      this.logOut = this.logOut.bind(this);
      this.state = {
        response: ""
      }
  }


  logOut(){
    fire.auth().signOut();
  }

  setRef = webcam => {
    this.webcam = webcam;
  };

  capture = () => {
      const imageSrc = this.webcam.getScreenshot();
      this.setState({post: imageSrc});
      this.handleSubmit();
     // f();


  };





  render() {
   return (

    <div >
    <Webcam
        audio={false}
        height={350}
        ref={this.setRef}
        screenshotFormat="image/jpeg"
        width={350}
    />
        {this.state.response}
    <button onClick={this.capture}>Play a song </button>
      <button onClick = {this.logOut} >logout</button>
      <button onClick = {this.handleLyricsSearch} >FIND LYRICS</button>


        <p>{this.state.responseToPost} </p>
    </div>
  );
}
}

export default ChatBot;
